/**
 * Elimina espacios en costados y convierte en mayusculas un nombre
 * @param {String} name Nombre a revisar
 */
const cleanName = (name) => {
    return name.toUpperCase().trim()
}

/**
 * Convierte un nombre en id segun estandar de Arzov
 * @param {String} name Nombre a convertir
 */
const nameToId = (name) => {
    // Elimina todos los espacios y convierte a minusculas
    return name.toLowerCase().replace(/\s+/g, '')
}

module.exports.cleanName = cleanName
module.exports.nameToId = nameToId