// functions

/**
 * Delete white spaces and apply uppercase to name input
 * @param {String} name Input name
 */
 const cleanName = (name) => {
    return name.toUpperCase().trim();
};


/**
 * Transform input name to an `id`
 * @param {String} name Input name
 */
const nameToId = (name) => {
    // Drop all whitespaces and transform to lowercase
    return name.toLowerCase().replace(/\s+/g, '');
};


/**
 * Indicates if an object is empty
 * @param {Object} data Object to handle
 */
const isObjectEmpty = (data) => {
    if (Object.entries(data).length > 0 && data.constructor === Object)
        return false;
    else return true;
};


// export modules

module.exports.cleanName = cleanName;
module.exports.nameToId = nameToId;
module.exports.isObjectEmpty = isObjectEmpty;
